// index.js
import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { Button, CssBaseline, Container, Typography } from '@mui/material';
import RegistrationForm from './components/Form';
import ExamForm from './components/ExamForm';
import PatientReport from './components/PatientReport';
import './styles.css';

const App = () => {
  const [showPatientForm, setShowPatientForm] = useState(true);
  const [showExamForm, setShowExamForm] = useState(false);
  const [patientData, setPatientData] = useState(null);

  const handleFormToggle = () => {
    setShowPatientForm(!showPatientForm);
    setShowExamForm(!showExamForm);
    setPatientData(null); // Limpar os dados do paciente ao alternar para o formulário
  };

  const handleShowPatientReport = (data) => {
    setPatientData(data);
    setShowPatientForm(false);
    setShowExamForm(false);
  };

  const buttonStyle = {
    variant: 'contained',
    color: 'primary',
    fullWidth: true,
  };

  return (
    <Container component="main" maxWidth="sm" style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', padding: '3rem', gap: '1rem' }}>
      <CssBaseline />
      <Typography variant="h4" color="primary" gutterBottom>
        {showPatientForm ? 'Registro de Pacientes' : 'Registro de Exames'}
      </Typography>
      {showPatientForm ? <RegistrationForm onShowPatientReport={handleShowPatientReport} /> : <ExamForm />}
      {patientData && <PatientReport patientData={patientData} handleReturnToForm={handleFormToggle} />}
      <Button onClick={handleFormToggle} {...buttonStyle}>
        {showPatientForm ? 'Clique para registrar um exame' : 'Clique para registrar um paciente'}
      </Button>
    </Container>
  );
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
