import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { Button, CssBaseline, Container, Typography } from '@mui/material';
import RegistrationForm from './components/Form';
import ExamForm from './components/ExamForm';
import PatientDetails from './components/PatientReport';
import './styles.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Import BrowserRouter as Router, Routes, and Route

const App = () => {
  const [showPatientForm, setShowPatientForm] = useState(true);
  const [showExamForm, setShowExamForm] = useState(false);

  const handleExamLinkClick = () => {
    setShowPatientForm(false);
    setShowExamForm(true);
  };

  const handlePatientLinkClick = () => {
    setShowExamForm(false);
    setShowPatientForm(true);
  };

  return (
    <Router>
      <Routes>
        <Route path="/registro">
          <Container component="main" maxWidth="sm" style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', padding: '3rem', gap: '1rem' }}>
            <CssBaseline />
            <Typography variant="h4" color="primary" gutterBottom>
              Registro de Pacientes
            </Typography>
            <Button component="a" href="/registro" variant="contained" color="primary" fullWidth>
              Clique para registrar um paciente
            </Button>
          </Container>
        </Route>

        <Route path="/exames">
          <Container component="main" maxWidth="sm" style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', padding: '3rem', gap: '1rem' }}>
            <CssBaseline />
            <Typography variant="h4" color="primary" gutterBottom>
              Registro de Exames
            </Typography>
            <Button component="a" href="/exames" variant="contained" color="primary" fullWidth>
              Clique para registrar um exame
            </Button>
          </Container>
        </Route>

        <Route path="/">
          <Container component="main" maxWidth="sm" style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', padding: '3rem', gap: '1rem' }}>
            <CssBaseline />
            <Typography variant="h4" color="primary" gutterBottom>
              {showPatientForm ? 'Registro de Pacientes' : 'Registro de Exames'}
            </Typography>
            {showPatientForm && (
              <Button component="a" href="/registro" variant="contained" color="primary" fullWidth>
                Clique para registrar um paciente
              </Button>
            )}
            {showExamForm && (
              <Button component="a" href="/exames" variant="contained" color="primary" fullWidth>
                Clique para registrar um exame
              </Button>
            )}
          </Container>
        </Route>
      </Routes>
    </Router>
  );
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
