import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { Button, CssBaseline, Container, Typography } from '@mui/material';
import RegistrationForm from './components/Form';
import ExamForm from './components/ExamForm';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom'; // Adicionado

const App = () => {
  const [showPatientForm, setShowPatientForm] = useState(true);
  const [showExamForm, setShowExamForm] = useState(false);

  const handleFormToggle = () => {
    setShowPatientForm(!showPatientForm);
    setShowExamForm(!showExamForm);
  };

  const buttonStyle = {
    variant: 'contained',
    color: 'primary',
    fullWidth: true,
  };

  return (
    <Container component="main" maxWidth="sm" style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', padding: '3rem', gap: '1rem' }}>
      <CssBaseline />
      <Typography variant="h4" color="primary" gutterBottom>
        {showPatientForm ? 'Registro de Pacientes' : 'Registro de Exames'}
      </Typography>
      {showPatientForm ? <RegistrationForm /> : <ExamForm />}
      <Button onClick={handleFormToggle} {...buttonStyle}>
        {showPatientForm ? 'Clique para registrar um exame' : 'Clique para registrar um paciente'}
      </Button>
      {/* Adicionado: Link para o relatório de paciente */}
      <Link to="/relatorio" style={{ textDecoration: 'none' }}>
        <Button {...buttonStyle} style={{ marginTop: '1rem' }}>
          Verificar relatório de paciente
        </Button>
      </Link>
    </Container>
  );
};

// Adicionado: Rota para o relatório de paciente
ReactDOM.render(
  <React.StrictMode>
    <Router>
      <Route path="/relatorio" component={RelatorioPaciente} />
      <App />
    </Router>
  </React.StrictMode>,
  document.getElementById('root')
);

// Adicionado: Componente RelatorioPaciente
const RelatorioPaciente = () => {
  // Adicione a lógica para obter e exibir o relatório do paciente aqui
  return (
    <Container component="main" maxWidth="sm" style={{ textAlign: 'center', padding: '3rem' }}>
      <Typography variant="h4" color="primary" gutterBottom>
        Relatório do Paciente
      </Typography>
      {/* Adicione a lógica para exibir o relatório do paciente aqui */}
    </Container>
  );
};
