import React, { useState } from 'react';
import styled from 'styled-components';

const FormContainer = styled.form`
    display: flex;
    flex-direction: column;
    max-width: 400px;
    margin: auto;
`;

const Label = styled.label`
    margin-bottom: 10px;
`;

const Input = styled.input`
    width: 100%;
    padding: 8px;
    margin-bottom: 15px;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-sizing: border-box;
`;

const SubmitButton = styled.button`
    background-color: #4caf50;
    color: white;
    padding: 10px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
`;

const ExamForm = () => {
    const [examData, setExamData] = useState({
        codigo: '',
        descricao: '',
        valor: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setExamData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://127.0.0.1:8000/api/cadastrar-exame', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(examData),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Exam registered successfully:', data);
                // You can update the UI or redirect the user after successful registration
            } else {
                console.error('Failed to register exam');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <FormContainer onSubmit={handleSubmit}>
            <Label>
                Code:
                <Input
                    type="text"
                    name="codigo"
                    value={examData.codigo}
                    onChange={handleInputChange}
                />
            </Label>
            <Label>
                Description:
                <Input
                    type="text"
                    name="descricao"
                    value={examData.descricao}
                    onChange={handleInputChange}
                />
            </Label>
            <Label>
                Value:
                <Input
                    type="text"
                    name="valor"
                    value={examData.valor}
                    onChange={handleInputChange}
                />
            </Label>
            <SubmitButton type="submit">Submit</SubmitButton>
        </FormContainer>
    );
};

export default ExamForm;
