// Exemplo: src/components/Form.js

import React, { useState } from 'react';
import axios from 'axios';

const Form = () => {
    const [formData, setFormData] = useState({
        // Defina o estado inicial do formulário
        nome_completo: '',
        idade: '',
        // Adicione mais campos conforme necessário
    });

    const handleChange = (e) => {
        // Atualize o estado do formulário conforme os campos são preenchidos
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Faça uma solicitação POST para a sua API Laravel
            const response = await axios.post('http://127.0.0.1:8000/api/cadastrar-paciente/', formData);

            // Manipule a resposta conforme necessário
            console.log('Resposta da API:', response.data);
        } catch (error) {
            // Trate erros de solicitação
            console.error('Erro ao enviar dados para a API:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Nome:
                <input type="text" name="nome_completo" value={formData.nome} onChange={handleChange} />
            </label>
            <br />
            <label>
                Idade:
                <input type="number" name="idade" value={formData.idade} onChange={handleChange} />
            </label>
            <br />
            {/* Adicione mais campos conforme necessário */}
            <button type="submit">Enviar</button>
        </form>
    );
};

export default Form;
