// Form.js

import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Form = () => {
  const history = useHistory();

  const [formData, setFormData] = useState({
    nome_completo: '',
    sexo: '',
    email: '',
    celular: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/api/cadastrar-paciente', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Patient registered successfully:', data);

        // Redirect to the patient report page with the generated number of attendance
        history.push(`/patient/${data.numero_atendimento}`);
      } else {
        console.error('Failed to register patient');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Full Name:
        <input
          type="text"
          name="nome_completo"
          value={formData.nome_completo}
          onChange={handleInputChange}
        />
      </label>
      {/* Add other form fields as needed */}
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Form;
