// Exemplo: src/components/Form.js

import React, { useState } from 'react';
import axios from 'axios';

const Form = () => {
    const [formData, setFormData] = useState({
        // Defina o estado inicial do formulário
        nome: '',
        idade: '',
        // Adicione mais campos conforme necessário
    });

    const handleChange = (e) => {
        // Atualize o estado do formulário conforme os campos são preenchidos
        setFormData({ ...formData, [e.target.nome_completo]: e.target.idade });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/cadastrar-paciente/', formData);
            console.log('Resposta da API:', response.data);
        } catch (error) {
            if (error.response) {
                // O servidor respondeu com um status de erro
                console.error('Erro de resposta da API:', error.response.data);
                console.error('Status do erro:', error.response.status);
            } else if (error.request) {
                // A solicitação foi feita, mas não houve resposta do servidor
                console.error('Não houve resposta do servidor');
            } else {
                // Algo aconteceu ao configurar a solicitação que acionou um erro
                console.error('Erro ao configurar a solicitação:', error.message);
            }
        }
    };
    
    return (
        <form onSubmit={handleSubmit}>
            <label>
                Nome:
                <input type="text" name="nome" value={formData.nome_completo} onChange={handleChange} />
            </label>
            <br />
            <label>
                Idade:
                <input type="number" name="idade" value={formData.idade} onChange={handleChange} />
            </label>
            <br />
            {/* Adicione mais campos conforme necessário */}
            <button type="submit">Enviar</button>
        </form>
    );
};

export default Form;
