import React, { useState } from 'react';
import styled from 'styled-components';

const FormContainer = styled.div`
    display: flex;
    flex-direction: column;
    max-width: 400px;
    margin: auto;
    background-color: black;
`;

const Label = styled.label`
    margin-bottom: 10px;
`;

const Input = styled.input`
    width: 100%;
    padding: 8px;
    margin-bottom: 15px;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-sizing: border-box;
`;

const SubmitButton = styled.button`
    background-color: #4caf50;
    color: white;
    padding: 10px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
`;

const RegistrationForm = () => {
    const [formData, setFormData] = useState({
        nome_completo: '',
        sexo: '',
        email: '',
        celular: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://127.0.0.1:8000/api/cadastrar-paciente', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Patient registered successfully:', data);
                // You can update the UI or redirect the user after successful registration
            } else {
                console.error('Failed to register patient');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <FormContainer className="container">
            <form onSubmit={handleSubmit}>
                <Label>
                    Full Name:
                    <Input
                        type="text"
                        name="nome_completo"
                        value={formData.nome_completo}
                        onChange={handleInputChange}
                    />
                </Label>
                <Label>
                    Gender:
                    <Input
                        type="text"
                        name="sexo"
                        value={formData.sexo}
                        onChange={handleInputChange}
                    />
                </Label>
                <Label>
                    Email:
                    <Input
                        type="text"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                    />
                </Label>
                <Label>
                    Phone:
                    <Input
                        type="text"
                        name="celular"
                        value={formData.celular}
                        onChange={handleInputChange}
                    />
                </Label>
                <SubmitButton type="submit">Submit</SubmitButton>
            </form>
        </FormContainer>
    );
};

export default RegistrationForm;
